import React, { useState } from 'react';
import axios from 'axios';

const DataInput = () => {
    const [inputData, setInputData] = useState('');
    const [responseData, setResponseData] = useState(null);
    const [error, setError] = useState('');
    const [selectedOptions, setSelectedOptions] = useState({
        alphabets: false,
        numbers: false,
        highestLowercase: false,
        userInfo: false
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Reset any previous errors
        try {
            const jsonData = JSON.parse(inputData); // Parse input JSON
            const response = await axios.post('http://localhost:3000/bfhl', jsonData);
            setResponseData(response.data);
        } catch (err) {
            console.error('Submission error:', err);
            setError('An error occurred while submitting data.');
            setResponseData(null);
        }
    };

    const handleOptionChange = (e) => {
        const { name, checked } = e.target;
        setSelectedOptions((prev) => ({ ...prev, [name]: checked }));
    };

    const renderResponse = () => {
        if (!responseData) return null;

        const output = {};
        if (selectedOptions.userInfo) {
            output.user_info = {
                user_id: responseData.user_id,
                email: responseData.email,
                roll_number: responseData.roll_number,
            };
        }
        if (selectedOptions.alphabets) output.alphabets = responseData.alphabets;
        if (selectedOptions.numbers) output.numbers = responseData.numbers;
        if (selectedOptions.highestLowercase) output.highestLowercase = responseData.highest_lowercase_alphabet;

        return <pre>{JSON.stringify(output, null, 2)}</pre>;
    };

    return (
        <div>
            <h1>Data Input</h1>
            <form onSubmit={handleSubmit}>
                <textarea
                    rows="5"
                    value={inputData}
                    onChange={(e) => setInputData(e.target.value)}
                    placeholder='Enter JSON here (e.g., {"data": ["A", "C", "Z", "c", "i"], "file_b64": "BASE_64_STRING"})'
                    required
                />
                <button type="submit">Submit</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <h2>Select Options</h2>
                <label>
                    <input
                        type="checkbox"
                        name="userInfo"
                        checked={selectedOptions.userInfo}
                        onChange={handleOptionChange}
                    />
                    User Info
                </label>
                <label>
                    <input
                        type="checkbox"
                        name="alphabets"
                        checked={selectedOptions.alphabets}
                        onChange={handleOptionChange}
                    />
                    Alphabets
                </label>
                <label>
                    <input
                        type="checkbox"
                        name="numbers"
                        checked={selectedOptions.numbers}
                        onChange={handleOptionChange}
                    />
                    Numbers
                </label>
                <label>
                    <input
                        type="checkbox"
                        name="highestLowercase"
                        checked={selectedOptions.highestLowercase}
                        onChange={handleOptionChange}
                    />
                    Highest Lowercase Alphabet
                </label>
            </div>
            <h2>Response</h2>
            {renderResponse()}
        </div>
    );
};

export default DataInput;
