import React, { useState } from 'react';
import axios from 'axios';

const DataInput = () => {
    const [inputData, setInputData] = useState('');
    const [responseData, setResponseData] = useState(null);
    const [error, setError] = useState('');
    const [selectedOptions, setSelectedOptions] = useState({
        alphabets: false,
        numbers: false,
        highestLowercase: false,
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); // Clear any previous error

        // Check for empty input
        if (!inputData.trim()) {
            setError('Input cannot be empty');
            return;
        }

        try {
            const jsonData = JSON.parse(inputData); // Parse the JSON input
            const response = await axios.post('http://localhost:3000/bfhl', jsonData);
            setResponseData(response.data);
        } catch (err) {
            if (err instanceof SyntaxError) {
                setError('Invalid JSON format. Please check your input.');
            } else {
                setError('An error occurred while submitting data.');
            }
            setResponseData(null);
        }
    };

    const handleOptionChange = (e) => {
        const { name, checked } = e.target;
        setSelectedOptions((prev) => ({ ...prev, [name]: checked }));
    };

    const renderResponse = () => {
        if (!responseData) return null;

        const output = {};
        if (selectedOptions.alphabets) output.alphabets = responseData.alphabets;
        if (selectedOptions.numbers) output.numbers = responseData.numbers;
        if (selectedOptions.highestLowercase) output.highestLowercase = responseData.highest_lowercase_alphabet;

        return <pre>{JSON.stringify(output, null, 2)}</pre>;
    };

    return (
        <div>
            <h1>Data Input</h1>
            <form onSubmit={handleSubmit}>
                <textarea
                    rows="5"
                    value={inputData}
                    onChange={(e) => setInputData(e.target.value)}
                    placeholder='Enter JSON here (e.g., {"data": ["A", "C", "Z", "c", "i"]})'
                    required
                />
                <button type="submit">Submit</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <h2>Select Options</h2>
                <label>
                    <input
                        type="checkbox"
                        name="alphabets"
                        checked={selectedOptions.alphabets}
                        onChange={handleOptionChange}
                    />
                    Alphabets
                </label>
                <label>
                    <input
                        type="checkbox"
                        name="numbers"
                        checked={selectedOptions.numbers}
                        onChange={handleOptionChange}
                    />
                    Numbers
                </label>
                <label>
                    <input
                        type="checkbox"
                        name="highestLowercase"
                        checked={selectedOptions.highestLowercase}
                        onChange={handleOptionChange}
                    />
                    Highest Lowercase Alphabet
                </label>
            </div>
            <h2>Response</h2>
            {renderResponse()}
        </div>
    );
};

export default DataInput;
